import React, {useEffect, useState} from "react";
import Navbar from "../components/Navbar";
import styled, {keyframes} from "styled-components";
import SearchMood from "../components/SearchMood";

import {AuthContext} from "../myContext/myAuthContext";

const AnimationName = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

const Container = styled.div`
  height: 92vh;
  width: 100%;
  background: linear-gradient(234deg, #a239ca, #0e0b16);
  background-size: 400% 400%;
  animation: ${AnimationName} 15s ease infinite;
`;



const Center = styled.div`
  padding-top: 10%;
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;
const ContentBox = styled.div`
    background-color: #0E0B16;
    padding: 1%;
    width:60%;
`;

const Title = styled.h1`
    color: #4717F6;
`;

const Playlist = styled.ul`
    list-style: none;
    padding-left: 0;
`;
const Song = styled.li`
    color: #A239CA;
    text-decoration: none;
    font-size: 18px;
    padding: 2px;
    margin: 0;
    &:hover {
      color: white;
      cursor: pointer;
      background-color: #4717F6;
    }
`;
const ViewPlaylist = () => {
    const playlistName = "My Mood Playlist";
    const [userID, setUserID] = useState("");

    const [token, setToken] = useState("");

    useEffect(() => {
        if (localStorage.getItem("token")) {
            setToken(localStorage.getItem("token"))
        }
    })

    useEffect(() => {
        if (token) {
            fetch("https://api.spotify.com/v1/me", {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            })
                .then((response) => response.json())
                .then((data) => {
                    setUserID(data.id);
                })
                .catch((error) => {
                    console.error("Error fetching user data:", error);
                })
        }
    }, [token]);


    const [playlistID, setPlaylistID] = useState("");
    const [playlists, setPlayists] = useState([]);


    useEffect(() => { 
        fetch(`https://api.spotify.com/v1/users/${userID}/playlists`, {
            method: 'GET',
            headers: {
                Authorization: `Bearer ${token}`
            },
        }).then(response => response.json())
            .then((data) => {
                setPlayists(data.items)
                if (playlists) {
                    for (let i = 0; i < 20; i++) {
                        if (playlistName === data.items[i].name) {
                            setPlaylistID(data.items[i].id);
                        }
                    }
                }
            })
            .catch((error) => {
                console.error("Error fetching playlists:", error);
            });

    }, [userID, token, playlistName])

    if (playlistID) {
        console.log(playlistID);
    }

    return(
        <div>
            <Navbar/>
            <Container>
                <Center>
                    <ContentBox>
                        <Title>Playlist Title</Title>
                        <Playlist>
                            {/*{playlist.map((song) => (*/}
                            {/*    <Song>{song.name}</Song>*/}
                            {/*))*/}

                            {/*}*/}
                        </Playlist>
                    </ContentBox>
                </Center>
            </Container>
        </div>
    )

}

export default ViewPlaylist;
