import React, {useEffect, useState} from "react";

const SearchMood = () => {
    const [moods, setMoods] = useState([]);
    const [searchInput, setSearchInput] = useState();

    useEffect(() => {
        // Fetch the moods from the JSON file or an API endpoint
        fetch("moods.json")
            .then((response) => response.json())
            .then((data) => setMoods(data.moods))
            .catch((error) => console.error(error));
    }, []);

    const handleSearch = (event) => {
        setSearchInput(event.target.value);
    }

    const filteredMoods = moods.filter((mood) =>
        mood.toLowerCase().includes(searchInput.toLowerCase())
    );

    return (
        <div>
            <input
                type="text"
                placeholder="Search for a music mood"
                value={searchInput}
                onChange={handleSearch}
            />
            <ul>
                {filteredMoods.map((mood, index) => (
                    <li key={index}>{mood}</li>
                ))}
            </ul>
        </div>
    );
};

export default SearchMood;
