import React, { useState, useEffect } from "react";
import axios from "axios";

const moodCategories = ["Happy", "Angsty", "Energetic", "Relaxed", "Sad"];

const SearchBox = () => {
    const [selectedMood, setSelectedMood] = useState("");
    const [searchResults, setSearchResults] = useState([]);

    useEffect(() => {
        if (selectedMood) {
            // Perform the search based on the selectedMood
            // You can use the selectedMood to filter or search for specific songs or playlists related to that mood
            // Example: Perform an API request using the selectedMood as a query parameter
            axios
                .get(`https://api.spotify.com/v1/search?q=${selectedMood}&type=track`)
                .then((response) => {
                    setSearchResults(response.data.tracks.items);
                })
                .catch((error) => {
                    console.error("Error fetching search results:", error);
                });
        } else {
            setSearchResults([]);
        }
    }, [selectedMood]);

    return (
        <div>
            <input
                type="text"
                placeholder="Enter a mood"
                value={selectedMood}
                onChange={(e) => setSelectedMood(e.target.value)}
            />

            {selectedMood && (
                <select value="" onChange={() => {}}>
                    <option value="" disabled>
                        Select a track
                    </option>
                    {searchResults.map((result) => (
                        <option key={result.id} value={result.id}>
                            {result.name}
                        </option>
                    ))}
                </select>
            )}

            {selectedMood && (
                <div className="dropdown">
                    {searchResults.map((result) => (
                        <div key={result.id} className="dropdown-item">
                            {result.name}
                        </div>
                    ))}
                </div>
            )}

        </div>
    );
};

export default SearchBox;
