import React, {useState} from "react";
import styled from "styled-components";
import {Link, useHistory} from "react-router-dom";
import {useEffect} from "react";

const Container = styled.div`
    height:60px;
    background-color: #0E0B16;
`;
const Wrapper = styled.div`
    padding: 10px 20px;
    display: flex;
    align-items: center;
    justify-content: space-between;
`;

const Left = styled.div`
  flex: 1;
  display: flex;
  align-items: flex-start;
`
const Right = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: flex-end;
`;
const Center = styled.div`
    flex: 1;
    text-align: center;
`;

const Logo = styled.h1`
    font-size: 32px;
    font-weight: bold;
    cursor: pointer;
    color: #A239CA;
    text-decoration: none;
    &:hover {
      color: #A239CA;
    }
`;

const MenuItem = styled(Link)`
    font-size: 16px;
    cursor: pointer;
    margin-left: 25px;
    text-decoration: none;
    color: #A239CA;
    &:hover {
      color: white;
    }
`;


const Navbar = () => {
    const CLIENT_ID = "69df94900568478790af02c965324d0f";
    const REDIRECT_URI = "http://localhost:3000/";
    const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize";
    const RESPONSE_TYPE = "token";

    const [token, setToken] = useState("");
    useEffect(() => {
        const hash = window.location.hash;
        let token = window.localStorage.getItem("token");

        //if we don't have a token and the hash is set
        if (!token && hash) {
            token = hash.substring(1).split("&").find(element => element.startsWith("access_token")).split("=")[1];

            //store token in local storage
            window.location.hash = "";
            window.localStorage.setItem("token", token);
            setToken(token);
        }
    }, [])

    const logout = () => {
        setToken("");
        window.localStorage.removeItem("token");
    }

    return(
    <Container>
        <Wrapper>
            <Left></Left>
            <Center>
                <Logo>Moosic</Logo>
            </Center>
            <Right>
                {!token ?
                    <MenuItem
                        to={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}`}>Login</MenuItem>
                    :
                    <MenuItem to={""}>Logout</MenuItem>
                }
            </Right>
        </Wrapper>
    </Container>
    )
}

export default Navbar;
