import React, {useEffect, useState} from "react";
import styled from "styled-components";

let moodData = require("./moods.json");

const Input = styled.input`
  background-color: #0E0B16;
  color: #A239CA;
  border: none;
  width: 100%;
  font-size: 25px;
  padding-top: 20px;
  outline: none;
`;

const Dropdown = styled.ul`
    list-style: none;
    padding-left: 0;
`;

const Result = styled.li`
    color: #A239CA;
    text-decoration: none;
    font-size: 18px;
    padding: 2px;
    margin: 0;
    &:hover {
      color: white;
      cursor: pointer;
      background-color: #4717F6;
    }
`;

const SearchMood = () => {
    const [userID, setUserID] = useState("");
    const token = window.localStorage.getItem("token");

    useEffect(() => {
        if (token) {
            fetch("https://api.spotify.com/v1/me", {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            })
                .then((response) => response.json())
                .then((data) => {
                    setUsername(data.id);
                })
                .catch((error) => {
                    console.error("Error fetching user data:", error);
                })
        }
    }, [token]);

    const playlistData = {
        name: "My Mood Playlist",
        description: "A playlist based on my current mood",
        public: false
    }


    const [searchInput, setSearchInput] = useState("");


    const handleSearch = (event) => {
        setSearchInput(event.target.value);
    }

    const filteredMoods = moodData.moods.filter((mood) =>
        mood.toLowerCase().includes(searchInput.toLowerCase())
    );

    return (
        <div>
            <Input
                type="text"
                placeholder="Enter your mood"
                value={searchInput}
                onChange={handleSearch}
            />
            {searchInput.length > 0 && (
            <Dropdown>
                {filteredMoods.map((mood, index) => (
                    <Result key={index}>{mood}</Result>
                ))}
            </Dropdown>
            )}
        </div>
    );
};

export default SearchMood;
